import { useState } from 'react'
import Card from './shared/Card'
import Button from './shared/Button'
import RatingSelect from './RatingSelect'
function FeedbackForm() {
    const [text,setText] = useState('')
    const [rating,setRating] = useState(10)
    const [message,setMessage] = useState('')
    const [btnDisabled,setbtnDisabled] = useState(true)

    const handleTextChange = (e) =>{
        if(text===''){
         setMessage(null);
         setbtnDisabled(true);
        }else if(text!=='' && text.trim().length <= 10){
            setMessage('The text should be at least 10 character long');
            setbtnDisabled(true)
        }else{
            setMessage(null);
            setbtnDisabled(false);
        }
        setText(e.target.value)

    }

    const handleSubmit = (e) =>{
        e.preventDefault();
        alert({text})
    }
  return (
    <Card>
        <form onSubmit={handleSubmit}>
            <h3>Please leave your rating!!!</h3>
            <RatingSelect select={(rating)=>setRating(rating)}></RatingSelect>
            <div className='input-group'>
                <input type="text" placeholder='Write a Review here' onChange={handleTextChange} value={text}/>
                <Button version="secondary" type="submit" isDisabled={btnDisabled}>Send</Button>
            </div>
        <div className="msg">
            {message}
        </div>       
        </form>
    </Card>
  )
}

export default FeedbackForm
